using System.Collections; using System.Collections.Generic; using UnityEngine;  public class Warp : MonoBehaviour { 	ParticleSystem system; 	bool play = true; 	// Use this for initialization 	void Start () { 		system = GetComponent<ParticleSystem> (); 		system.Stop(); 	}  	// Update is called once per frame 	void Update () { 		if (GameObject.Find ("PlayerShip") != null) 			transform.position = GameObject.Find ("PlayerShip").transform.position; 		if (GameObject.Find ("PlayerShip") == null && GameState.jumps > 0) { 			if(!system.isEmitting && play){ 				play = false; 				system.Play (); 				Invoke("WarpIn",3); 			} 		} 	}  	void WarpIn(){ 		system.Stop (); 		system.Play (); 		play = true; 	} }  